import {Pipe, PipeTransform} from '@angular/core';

@Pipe({
  name: 'dataValues'
})
export class DataPipe implements PipeTransform {
  transform(obj: any) {
    let result = [];
    for (var key in obj) {
      if (obj.hasOwnProperty(key)) {
        result.push(obj[key]);
      }
    }
    // console.log(result);
    return result;
  }
}