import{Pipe,PipeTransform} from '@angular/core'
import {TableComponent} from './table.component'
@Pipe({
    name:"search"
})
export class SearchPipe implements PipeTransform{
   transform(value, search: string) {
        // console.log(search);
        
        
        if(!search || search === '') {
            return value;
        }
        else {
            let result = value.filter((item) => {
                 return Object.keys(item).some(key => item[key].indexOf(search) !== -1);
            });
            
        // console.log(result);
        return result;
        }
      }
    }

